import React, { useState, useRef, useEffect } from "react";
import { FaPlus, FaHome, FaInbox, FaSignOutAlt } from "react-icons/fa";
import { useNavigate } from "react-router-dom"; // Import useNavigate

// Utility function to generate initials
const getInitials = (firstName, lastName = "") => {
  return `${firstName[0] || ""}${lastName[0] || ""}`.toUpperCase();
};

const Some = () => {
  const [notes, setNotes] = useState([]);
  const [newNoteTitle, setNewNoteTitle] = useState("");
  const [isAddingNote, setIsAddingNote] = useState(false);
  const [selectedNoteIndex, setSelectedNoteIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [noteContent, setNoteContent] = useState("");
  const [isMenuOpen, setIsMenuOpen] = useState(null);
  const [isRenaming, setIsRenaming] = useState(null);
  const [renameInput, setRenameInput] = useState("");

  // Create a ref for the new note input field
  const newNoteInputRef = useRef(null);

  const navigate = useNavigate(); // Initialize useNavigate for redirection

  // User information (replace with actual login/signup user data)
  const user = {
    id: "user123", // This should come from login/signup, uniquely identifying the user
    firstName: "Jithendranath",
    lastName: "",
  };
  const initials = getInitials(user.firstName, user.lastName);

  // Load notes from localStorage specific to the user when the component mounts
  useEffect(() => {
    const storedNotes = JSON.parse(localStorage.getItem(`notes_${user.id}`));
    if (storedNotes) {
      setNotes(storedNotes);
    }
  }, [user.id]);

  // Save notes to localStorage specific to the user whenever the notes array changes
  useEffect(() => {
    if (notes.length > 0) {
      localStorage.setItem(`notes_${user.id}`, JSON.stringify(notes));
    }
  }, [notes, user.id]);

  // Filter notes based on search query
  const filteredNotes = notes.filter((note) =>
    note.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Handle adding a new note
  const addNewNote = () => {
    if (newNoteTitle.trim()) {
      const newNote = {
        title: newNoteTitle,
        content: "",
      };
      setNotes([...notes, newNote]);
      setNewNoteTitle("");
      setIsAddingNote(false);
      setSelectedNoteIndex(notes.length);
      setNoteContent("");
    }
  };

  // Handle selecting a note to open in the right panel
  const openNote = (index) => {
    setSelectedNoteIndex(index);
    setNoteContent(notes[index].content);
  };

  // Handle updating the content of the selected note
  const updateNoteContent = (content) => {
    const updatedNotes = [...notes];
    updatedNotes[selectedNoteIndex].content = content;
    setNotes(updatedNotes);
    setNoteContent(content);
  };

  // Handle renaming a note in the sidebar
  const handleRename = (index) => {
    const updatedNotes = [...notes];
    updatedNotes[index].title = renameInput;
    setNotes(updatedNotes);
    setIsRenaming(null);
    setIsMenuOpen(null);
  };

  // Handle deleting a note
  const deleteNote = (index) => {
    const updatedNotes = notes.filter((_, i) => i !== index);
    setNotes(updatedNotes);
    setSelectedNoteIndex(null);
    setIsMenuOpen(null);
  };

  // When isAddingNote is true, focus the input field
  useEffect(() => {
    if (isAddingNote && newNoteInputRef.current) {
      newNoteInputRef.current.focus();
    }
  }, [isAddingNote]);

  // Handle logout functionality
  const handleLogout = () => {
    // Clear user-specific data, e.g., from localStorage
    localStorage.removeItem(`notes_${user.id}`); // Clear notes
    localStorage.removeItem("user"); // Remove any stored user data

    // Redirect to login page (replace '/login' with your actual login route)
    navigate("/");
  };

  return (
    <div className="flex min-h-screen">
      {/* Sidebar */}
      <div className="w-64 bg-gray-800 p-4 flex flex-col justify-between">
        {/* Top Section */}
        <div>
          {/* User Info */}
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center">
              <div className="w-10 h-10 flex items-center justify-center bg-gray-600 text-white rounded-full mr-3">
                <span className="text-lg font-bold">{initials}</span>
              </div>
              <span className="text-sm font-semibold text-white">
                {user.firstName}
              </span>
            </div>

            {/* Button to Add New Note */}
            <button
              className="bg-blue-600 text-white p-2 rounded hover:bg-blue-500"
              onClick={() => setIsAddingNote(true)}
            >
              <FaPlus />
            </button>
          </div>

          {/* Search Bar */}
          <div className="mb-4">
            <input
              type="text"
              placeholder="Search notes..."
              className="w-full p-2 rounded bg-gray-700 text-white"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>

          {/* Sidebar Menu */}
          <nav className="space-y-4">
            <button className="flex items-center text-white text-sm hover:bg-gray-700 p-2 rounded">
              <FaHome />
              <span className="ml-2">Home</span>
            </button>
            <button className="flex items-center text-white text-sm hover:bg-gray-700 p-2 rounded">
              <FaInbox />
              <span className="ml-2">Inbox</span>
            </button>
          </nav>

          {/* Private Notes Section */}
          <div className="mt-8">
            <button className="flex items-center justify-between w-full text-sm uppercase text-gray-400 mb-3">
              Private Notes
            </button>

            <ul className="space-y-4">
              {filteredNotes.length > 0 ? (
                filteredNotes.map((note, index) => (
                  <li
                    key={index}
                    className="flex justify-between items-center hover:bg-gray-700 p-2 rounded cursor-pointer"
                  >
                    {/* Note Title */}
                    {isRenaming === index ? (
                      <input
                        type="text"
                        className="bg-gray-800 text-white p-2 rounded"
                        value={renameInput}
                        onChange={(e) => setRenameInput(e.target.value)}
                        onBlur={() => handleRename(index)}
                        onKeyDown={(e) =>
                          e.key === "Enter" && handleRename(index)
                        }
                        autoFocus
                      />
                    ) : (
                      <div onClick={() => openNote(index)} className="flex-1">
                        {note.title}
                      </div>
                    )}

                    {/* 3 Dots Menu */}
                    <div className="relative">
                      <button
                        className="text-white"
                        onClick={() =>
                          setIsMenuOpen(isMenuOpen === index ? null : index)
                        }
                      >
                        â‹®
                      </button>

                      {isMenuOpen === index && (
                        <div className="absolute right-0 mt-2 w-32 bg-gray-700 text-white rounded shadow-lg">
                          <button
                            className="w-full text-left px-4 py-2 hover:bg-gray-600"
                            onClick={() => {
                              setIsRenaming(index);
                              setRenameInput(note.title);
                            }}
                          >
                            Rename
                          </button>
                          <button
                            className="w-full text-left px-4 py-2 hover:bg-gray-600"
                            onClick={() => deleteNote(index)}
                          >
                            Delete
                          </button>
                        </div>
                      )}
                    </div>
                  </li>
                ))
              ) : (
                <li className="text-gray-500">No notes found</li>
              )}
            </ul>
          </div>

          {/* Input Field to Add a New Note */}
          {isAddingNote && (
            <div className="mt-4">
              <input
                type="text"
                placeholder="New note title"
                className="w-full p-2 rounded bg-gray-800 text-white"
                value={newNoteTitle}
                onChange={(e) => setNewNoteTitle(e.target.value)}
                ref={newNoteInputRef} // Attach the ref to the input
              />
              <div className="flex justify-between mt-2">
                <button
                  className="bg-blue-600 text-white p-2 rounded hover:bg-blue-500"
                  onClick={addNewNote}
                >
                  Add Note
                </button>
                <button
                  className="bg-gray-600 text-white p-2 rounded hover:bg-gray-500"
                  onClick={() => setIsAddingNote(false)}
                >
                  Cancel
                </button>
              </div>
            </div>
          )}
        </div>

        {/* Logout Button */}
        <button
          className="flex items-center text-white text-sm bg-gray-900 hover:bg-red-600 p-2 rounded"
          onClick={handleLogout} // Attach the logout handler here
        >
          <FaSignOutAlt />
          <span className="ml-2">Logout</span>
        </button>
      </div>

      {/* Main Content Area */}
       <div className="flex-1 flex flex-col p-6">
        <div className="flex-1 flex flex-col bg-gray-800 rounded-lg shadow-lg overflow-hidden">
          {selectedNoteIndex !== null ? (
            <>
              <div className="p-4 border-b bg-gray-900 ">
                <h2 className="text-xl font-semibold rounded-lg">
                  {notes[selectedNoteIndex].title}
                </h2>
              </div>
              <textarea
                className="flex-1 p-4 bg-gray-800 resize-none focus:outline-none rounded-lg"
                value={noteContent}
                onChange={(e) => updateNoteContent(e.target.value)}
              />
            </>
          ) : (
            <div className="flex-1 flex items-center justify-center rounded-lg">
              <p className="text-gray-500">No note selected</p>
            </div>
          )}
        </div>
      </div> 
    </div>
  );
};

export default Some;
